import React, { useState, useEffect, Fragment } from "react";
import { Dialog, Transition } from "@headlessui/react";
import {
  CheckIcon,
  EyeIcon,
  TrashIcon,
  PencilAltIcon,
  ExclamationIcon,
} from "@heroicons/react/outline";
import Loader from "../../components/shared/Loader";
import {
  addUser,
  deleteUser,
  getAllUsers,
  updateUser,
} from "../../api/usersApi";
import {
  isAuthenticated,
  selectCurrentUser,
} from "../../redux/store/authStore";
import { useSelector } from "react-redux";

import photo from "../../assets/user1.png";
import photo1 from "../../assets/entre2.png";

export default function ClientPage() {
  const currentUser = useSelector(selectCurrentUser);
  // console.log("user", currentUser);
  const [clients, setClients] = useState([]);
  const [id, setId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [open, setOpen] = useState(false);
  const [open1, setOpen1] = useState(false);
  const [open2, setOpen2] = useState(false);
  const [open3, setOpen3] = useState(false);
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    address: "",
    postalCode: "",
    city: "",
    tel: "",
    id: false,
  });
  useEffect(() => {
    getData();
  }, []);
  const getData = async () => {
    const data = await getAllUsers();
    setClients(data.data.users);
    console.log(data.data.users);
    setLoading(false);
  };

  function updateField(item, value) {
    setForm({
      ...form,
      [item]: value,
    });
  }

  const onSubmit = async () => {
    setOpen(false);
    setLoading(true);
    if (form.id) {
      await updateUser(form.id, form);
    } else {
      console.log("TES 1");
      await addUser(form);
    }
    getData();
    setMessage(" Client crée avec succès");
    setOpen1(true);
    setForm({
      firstName: "",
      lastName: "",
      email: "",
      address: "",
      postalCode: "",
      city: "",
      tel: "",
      isProfessional: false,
      isEntreprise: false,
    });
  };

  const onDelete = async () => {
    setOpen2(false);
    setLoading(true);
    await deleteUser(id);
    setMessage("Client supprimer avec succès");
    setLoading(false);
    window.location.reload();
  };

  return (
    <div className="py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 md:px-8">
        <h1 className="mb-3 text-2xl font-semibold text-gray-900">
          Liste des admins
        </h1>
        <button
          onClick={() => setOpen(true)}
          type="button"
          className="mt3 mb-3 inline-flex items-center px-4 py-2 border border-transparent text-base font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          Ajouter un utilisateur
        </button>
      </div>
      {loading ? (
        <Loader />
      ) : (
        <div className="mt-5 max-w-7xl mx-auto px-4 sm:px-6 md:px-8">
          {clients.length === 0 ? (
            <h2 className="text-center mt-10 mb-8 text-lg leading-6 font-medium text-gray-900">
              Pas d'admin en cours
            </h2>
          ) : (
            <div className="flex flex-col">
              <div className="-my-2 overflow-x-auto sm:-mx-6 lg:-mx-8">
                <div className="py-2 align-middle inline-block min-w-full sm:px-6 lg:px-8">
                  <div className="shadow overflow-hidden border-b border-gray-200 sm:rounded-lg">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Nom et Email
                          </th>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Addresse
                          </th>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Tél.
                          </th>
                          <th scope="col" className="relative px-6 py-3">
                            <span className="sr-only">Edit</span>
                          </th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {clients.map((client) => (
                          <tr key={client._id}>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <div className="flex items-center">
                                <div className="flex-shrink-0 h-10 w-10">
                                  <img
                                    className="h-15 w-15 rounded-full"
                                    src={client.isEntreprise ? photo1 : photo}
                                    alt=""
                                  />
                                </div>
                                <div className="ml-4">
                                  <div className="text-sm font-medium text-gray-900">
                                    {client.name}
                                  </div>
                                  <div className="text-sm text-gray-500">
                                    {client.email}
                                  </div>
                                </div>
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <div className="text-sm text-gray-900">
                                {client.address}
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <div className="text-sm text-gray-900">
                                {client.tel}
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                                {client.postalCode}
                              </span>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                              {currentUser._id === client._id ? null : (
                                <button
                                  type="button"
                                  className="inline-flex items-center p-1.5 border border-red rounded-full shadow-sm text-white bg-red-600 hover:bg-red-900 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                                  onClick={() => {
                                    setId(client._id);
                                    setOpen2(true);
                                  }}
                                >
                                  <TrashIcon
                                    className="h-5 w-5"
